660 // Functions for communicating with ISO/IEC 14433-4 cards
662 
663 MFRC522::StatusCode MFRC522Extended::TCL_Transceive(PcbBlock *send, PcbBlock *back)
664 {
665     MFRC522::StatusCode result;
666     byte inBuffer[FIFO_SIZE];
667     byte inBufferSize = FIFO_SIZE;
668     byte outBuffer[send->inf.size + 5]; // PCB + CID + NAD + INF + EPILOGUE (CRC)
669     byte outBufferOffset = 1;
670     byte inBufferOffset = 1;
671 
672     // Set the PCB byte
673     outBuffer[0] = send->prologue.pcb;
674 
675     // Set the CID byte if available
676     if (send->prologue.pcb & 0x08) {
677         outBuffer[outBufferOffset] = send->prologue.cid;
678         outBufferOffset++;
679     }
680 
681     // Set the NAD byte if available
682     if (send->prologue.pcb & 0x04) {
683         outBuffer[outBufferOffset] = send->prologue.nad;
684         outBufferOffset++;
685     }
686 
687     // Copy the INF field if available
688     if (send->inf.size > 0) {
689         memcpy(&outBuffer[outBufferOffset], send->inf.data, send->inf.size);
690         outBufferOffset += send->inf.size;
691     }
692 
693     // Is the CRC enabled for transmission?
694     byte txModeReg = PCD_ReadRegister(TxModeReg);
695     if ((txModeReg & 0x80) != 0x80) {
696         // Calculate CRC_A
697         result = PCD_CalculateCRC(outBuffer, outBufferOffset, &outBuffer[outBufferOffset]);
698         if (result != STATUS_OK) {
699             return result;
700         }
701 
702         outBufferOffset += 2;
703     }
704 
705     // Transceive the block
706     result = PCD_TransceiveData(outBuffer, outBufferOffset, inBuffer, &inBufferSize);
707     if (result != STATUS_OK) {
708         return result;
709     }
710 
711     // We want to turn the received array back to a PcbBlock
712     back->prologue.pcb = inBuffer[0];
713 
714     // CID byte is present?
715     if (send->prologue.pcb & 0x08) {
716         back->prologue.cid = inBuffer[inBufferOffset];
717         inBufferOffset++;
718     }
719 
720     // NAD byte is present?
721     if (send->prologue.pcb & 0x04) {
722         back->prologue.nad = inBuffer[inBufferOffset];
723         inBufferOffset++;
724     }
725 
726     // Check if CRC is taken care of by MFRC522
727     byte rxModeReg = PCD_ReadRegister(TxModeReg);
728     if ((rxModeReg & 0x80) != 0x80) {
729         Serial.print("CRC is not taken care of by MFRC522: ");
730         Serial.println(rxModeReg, HEX);
731 
732         // Check the CRC
733         // We need at least the CRC_A value.
734         if ((int)(inBufferSize - inBufferOffset) < 2) {
735             return STATUS_CRC_WRONG;
736         }
737 
738         // Verify CRC_A - do our own calculation and store the control in controlBuffer.
739         byte controlBuffer[2];
740         MFRC522::StatusCode status = PCD_CalculateCRC(inBuffer, inBufferSize - 2, controlBuffer);
741         if (status != STATUS_OK) {
742             return status;
743         }
744 
745         if ((inBuffer[inBufferSize - 2] != controlBuffer[0]) || (inBuffer[inBufferSize - 1] != controlBuffer[1])) {
746             return STATUS_CRC_WRONG;
747         }
748 
749         // Take away the CRC bytes
750         inBufferSize -= 2;
751     }
752 
753     // Got more data?
754     if (inBufferSize > inBufferOffset) {
755         if ((inBufferSize - inBufferOffset) > back->inf.size) {
756             return STATUS_NO_ROOM;
757         }
758 
759         memcpy(back->inf.data, &inBuffer[inBufferOffset], inBufferSize - inBufferOffset);
760         back->inf.size = inBufferSize - inBufferOffset;
761     } else {
762         back->inf.size = 0;
763     }
764 
765     // If the response is a R-Block check NACK
766     if (((inBuffer[0] & 0xC0) == 0x80) && (inBuffer[0] & 0x20)) {
767         return STATUS_MIFARE_NACK;
768     }
769     
770     return result;
771 }
775 MFRC522::StatusCode MFRC522Extended::TCL_Transceive(TagInfo *tag, byte *sendData, byte sendLen, byte *backData, byte *backLen)
776 {
777     MFRC522::StatusCode result;
778 
779     PcbBlock out;
780     PcbBlock in;
781     byte outBuffer[FIFO_SIZE];
782     byte outBufferSize = FIFO_SIZE;
783     byte totalBackLen = *backLen;
784 
785     // This command sends an I-Block
786     out.prologue.pcb = 0x02;
787 
788     if (tag->ats.tc1.supportsCID) {
789         out.prologue.pcb |= 0x08;
790         out.prologue.cid = 0x00;    // CID is curentlly hardcoded as 0x00
791     }
792 
793     // This command doe not support NAD
794     out.prologue.pcb &= 0xFB;
795     out.prologue.nad = 0x00;
796 
797     // Set the block number
798     if (tag->blockNumber) {
799         out.prologue.pcb |= 0x01;
800     }
801 
802     // Do we have data to send?
803     if (sendData && (sendLen > 0)) {
804         out.inf.size = sendLen;
805         out.inf.data = sendData;
806     } else {
807         out.inf.size = 0;
808         out.inf.data = NULL;
809     }
810 
811     // Initialize the receiving data
812     // TODO Warning: Value escapes the local scope
813     in.inf.data = outBuffer;
814     in.inf.size = outBufferSize;
815 
816     result = TCL_Transceive(&out, &in);
817     if (result != STATUS_OK) {
818         return result;
819     }
820 
821     // Swap block number on success
822     tag->blockNumber = !tag->blockNumber;
823 
824     if (backData != nullptr && backLen !=nullptr) {
825         if (*backLen < in.inf.size)
826             return STATUS_NO_ROOM;
827 
828         *backLen = in.inf.size;
829         memcpy(backData, in.inf.data, in.inf.size);
830     }
831 
832     // Check chaining
833     if ((in.prologue.pcb & 0x10) == 0x00)
834         return result;
835 
836     // Result is chained
837     // Send an ACK to receive more data
838     // TODO: Should be checked I've never needed to send an ACK
839     while (in.prologue.pcb & 0x10) {
840         byte ackData[FIFO_SIZE];
841         byte ackDataSize = FIFO_SIZE;
842 
843         result = TCL_TransceiveRBlock(tag, true, ackData, &ackDataSize);
844         if (result != STATUS_OK)
845             return result;
846 
847         if (backData != nullptr && backLen != nullptr) {
848             if ((*backLen + ackDataSize) > totalBackLen)
849                 return STATUS_NO_ROOM;
850 
851             memcpy(&(backData[*backLen]), ackData, ackDataSize);
852             *backLen += ackDataSize;
853         }
854     }
855     
856     return result;
857 } // End TCL_Transceive()
858 
862 MFRC522::StatusCode MFRC522Extended::TCL_TransceiveRBlock(TagInfo *tag, bool ack, byte *backData, byte *backLen)
863 {
864     MFRC522::StatusCode result;
865 
866     PcbBlock out;
867     PcbBlock in;
868     byte outBuffer[FIFO_SIZE];
869     byte outBufferSize = FIFO_SIZE;
870 
871     // This command sends an R-Block
872     if (ack)
873         out.prologue.pcb = 0xA2;    // ACK
874     else 
875         out.prologue.pcb = 0xB2;    // NAK
876     
877 
878     if (tag->ats.tc1.supportsCID) {
879         out.prologue.pcb |= 0x08;
880         out.prologue.cid = 0x00;    // CID is curentlly hardcoded as 0x00
881     }
882 
883     // This command doe not support NAD
884     out.prologue.pcb &= 0xFB;
885     out.prologue.nad = 0x00;
886 
887     // Set the block number
888     if (tag->blockNumber) {
889         out.prologue.pcb |= 0x01;
890     }
891 
892     // No INF data for R-Block
893     out.inf.size = 0;
894     out.inf.data = NULL;
895 
896     // Initialize the receiving data
897     // TODO Warning: Value escapes the local scope
898     in.inf.data = outBuffer;
899     in.inf.size = outBufferSize;
900 
901     result = TCL_Transceive(&out, &in);
902     if (result != STATUS_OK) {
903         return result;
904     }
905 
906     // Swap block number on success
907     tag->blockNumber = !tag->blockNumber;
908 
909     if (backData && backLen) {
910         if (*backLen < in.inf.size)
911             return STATUS_NO_ROOM;
912 
913         *backLen = in.inf.size;
914         memcpy(backData, in.inf.data, in.inf.size);
915     }
916     
917     return result;
918 } // End TCL_TransceiveRBlock()
