923 MFRC522::StatusCode MFRC522Extended::TCL_Deselect(TagInfo *tag)
924 {
925     MFRC522::StatusCode result;
926     byte outBuffer[4];
927     byte outBufferSize = 1;
928     byte inBuffer[FIFO_SIZE];
929     byte inBufferSize = FIFO_SIZE;
930 
931     outBuffer[0] = 0xC2;
932     if (tag->ats.tc1.supportsCID)
933     {
934         outBuffer[0] |= 0x08;
935         outBuffer[1] = 0x00;  // CID is hardcoded
936         outBufferSize = 2;
937     }
938 
939     result = PCD_TransceiveData(outBuffer, outBufferSize, inBuffer, &inBufferSize);
940     if (result != STATUS_OK) {
941         return result;
942     }
943 
944     // TODO:Maybe do some checks? In my test it returns: CA 00 (Same data as I sent to my card)
945 
946     return result;
947 } // End TCL_Deselect()
