950 // Support functions
952 
958 MFRC522::PICC_Type MFRC522Extended::PICC_GetType(TagInfo *tag       
959 ) {
960     // http://www.nxp.com/documents/application_note/AN10833.pdf 
961     // 3.2 Coding of Select Acknowledge (SAK)
962     // ignore 8-bit (iso14443 starts with LSBit = bit 1)
963     // fixes wrong type for manufacturer Infineon (http://nfc-tools.org/index.php?title=ISO14443A)
964     byte sak = tag->uid.sak & 0x7F;
965     switch (sak) {
966         case 0x04:  return PICC_TYPE_NOT_COMPLETE;  // UID not complete
967         case 0x09:  return PICC_TYPE_MIFARE_MINI;
968         case 0x08:  return PICC_TYPE_MIFARE_1K;
969         case 0x18:  return PICC_TYPE_MIFARE_4K;
970         case 0x00:  return PICC_TYPE_MIFARE_UL;
971         case 0x10:
972         case 0x11:  return PICC_TYPE_MIFARE_PLUS;
973         case 0x01:  return PICC_TYPE_TNP3XXX;
974         case 0x20:
975             if (tag->atqa == 0x0344)
976                 return PICC_TYPE_MIFARE_DESFIRE;
977             return PICC_TYPE_ISO_14443_4;
978         case 0x40:  return PICC_TYPE_ISO_18092;
979         default:    return PICC_TYPE_UNKNOWN;
980     }
981 } // End PICC_GetType()
982 
988 void MFRC522Extended::PICC_DumpToSerial(TagInfo *tag)
989 {
990     MIFARE_Key key;
991 
992     // Dump UID, SAK and Type
993     PICC_DumpDetailsToSerial(tag);
994 
995     // Dump contents
996     PICC_Type piccType = MFRC522::PICC_GetType(tag->uid.sak);
997     switch (piccType) {
998         case PICC_TYPE_MIFARE_MINI:
999         case PICC_TYPE_MIFARE_1K:
1000         case PICC_TYPE_MIFARE_4K:
1001             // All keys are set to FFFFFFFFFFFFh at chip delivery from the factory.
1002             for (byte i = 0; i < 6; i++) {
1003                 key.keyByte[i] = 0xFF;
1004             }
1005             PICC_DumpMifareClassicToSerial(&tag->uid, piccType, &key);
1006             break;
1007         
1008         case PICC_TYPE_MIFARE_UL:
1009             PICC_DumpMifareUltralightToSerial();
1010             break;
1011         
1012         case PICC_TYPE_ISO_14443_4:
1013         case PICC_TYPE_MIFARE_DESFIRE:
1014             PICC_DumpISO14443_4(tag);
1015             Serial.println(F("Dumping memory contents not implemented for that PICC type."));
1016             break;
1017         case PICC_TYPE_ISO_18092:
1018         case PICC_TYPE_MIFARE_PLUS:
1019         case PICC_TYPE_TNP3XXX:
1020             Serial.println(F("Dumping memory contents not implemented for that PICC type."));
1021             break;
1022         
1023         case PICC_TYPE_UNKNOWN:
1024         case PICC_TYPE_NOT_COMPLETE:
1025         default:
1026             break; // No memory dump here
1027     }
1028 
1029     Serial.println();
1030     PICC_HaltA(); // Already done if it was a MIFARE Classic PICC.
1031 }
1032 
1036 void MFRC522Extended::PICC_DumpDetailsToSerial(TagInfo *tag 
1037 ) {
1038     // ATQA
1039     Serial.print(F("Card ATQA:"));
1040     if (((tag->atqa & 0xFF00u) >> 8) < 0x10)
1041         Serial.print(F(" 0"));
1042     Serial.print((tag->atqa & 0xFF00u) >> 8, HEX);
1043     if ((tag->atqa & 0x00FFu) < 0x10)
1044         Serial.print(F("0"));
1045     else
1046         Serial.print(F(" "));
1047     Serial.println(tag->atqa & 0x00FFu, HEX);
1048 
1049     // UID
1050     Serial.print(F("Card UID:"));
1051     for (byte i = 0; i < tag->uid.size; i++) {
1052         if (tag->uid.uidByte[i] < 0x10)
1053             Serial.print(F(" 0"));
1054         else
1055             Serial.print(F(" "));
1056         Serial.print(tag->uid.uidByte[i], HEX);
1057     }
1058     Serial.println();
1059 
1060     // SAK
1061     Serial.print(F("Card SAK: "));
1062     if (tag->uid.sak < 0x10)
1063         Serial.print(F("0"));
1064     Serial.println(tag->uid.sak, HEX);
1065 
1066     // (suggested) PICC type
1067     PICC_Type piccType = PICC_GetType(tag);
1068     Serial.print(F("PICC type: "));
1069     Serial.println(PICC_GetTypeName(piccType));
1070 } // End PICC_DumpDetailsToSerial()
1071 
1075 void MFRC522Extended::PICC_DumpISO14443_4(TagInfo *tag)
1076 {
1077     // ATS
1078     if (tag->ats.size > 0x00) { // The first byte is the ATS length including the length byte
1079         Serial.print(F("Card ATS:"));
1080         for (byte offset = 0; offset < tag->ats.size; offset++) {
1081             if (tag->ats.data[offset] < 0x10)
1082                 Serial.print(F(" 0"));
1083             else
1084                 Serial.print(F(" "));
1085             Serial.print(tag->ats.data[offset], HEX);
1086         }
1087         Serial.println();
1088     }
1089     
1090 } // End PICC_DumpISO14443_4
