1102 bool MFRC522Extended::PICC_IsNewCardPresent() {
1103     byte bufferATQA[2];
1104     byte bufferSize = sizeof(bufferATQA);
1105 
1106     // Reset baud rates
1107     PCD_WriteRegister(TxModeReg, 0x00);
1108     PCD_WriteRegister(RxModeReg, 0x00);
1109     // Reset ModWidthReg
1110     PCD_WriteRegister(ModWidthReg, 0x26);
1111 
1112     MFRC522::StatusCode result = PICC_RequestA(bufferATQA, &bufferSize);
1113 
1114     if (result == STATUS_OK || result == STATUS_COLLISION) {
1115         tag.atqa = ((uint16_t)bufferATQA[1] << 8) | bufferATQA[0];
1116         tag.ats.size = 0;
1117         tag.ats.fsc = 32;   // default FSC value
1118 
1119         // Defaults for TA1
1120         tag.ats.ta1.transmitted = false;
1121         tag.ats.ta1.sameD = false;
1122         tag.ats.ta1.ds = MFRC522Extended::BITRATE_106KBITS;
1123         tag.ats.ta1.dr = MFRC522Extended::BITRATE_106KBITS;
1124 
1125         // Defaults for TB1
1126         tag.ats.tb1.transmitted = false;
1127         tag.ats.tb1.fwi = 0;    // TODO: Don't know the default for this!
1128         tag.ats.tb1.sfgi = 0;   // The default value of SFGI is 0 (meaning that the card does not need any particular SFGT)
1129 
1130         // Defaults for TC1
1131         tag.ats.tc1.transmitted = false;
1132         tag.ats.tc1.supportsCID = true;
1133         tag.ats.tc1.supportsNAD = false;
1134 
1135         memset(tag.ats.data, 0, FIFO_SIZE - 2);
1136 
1137         tag.blockNumber = false;
1138         return true;
1139     }
1140     return false;
1141 } // End PICC_IsNewCardPresent()
