1151 bool MFRC522Extended::PICC_ReadCardSerial() {
1152     MFRC522::StatusCode result = PICC_Select(&tag.uid);
1153 
1154     // Backward compatibility
1155     uid.size = tag.uid.size;
1156     uid.sak = tag.uid.sak;
1157     memcpy(uid.uidByte, tag.uid.uidByte, sizeof(tag.uid.uidByte));
1158 
1159     return (result == STATUS_OK);
1160 } // End
